import React from "react"

import Joke from "./component/Joke"
import TodoItem from "./component/TodoItem"
import JokeData from "./component/JokeData"
import productData from "./component/ProductData"
import Product from "./component/Product"
import todoItemData from "./component/todoItemData"
import ChangingStateExample from "./component/ChangingStateExample"

/*
function App() {
    const jokeData = JokeData.map(d => <Joke key = {d.id}
                                             question = {d.question}
                                             punch = {d.punch}/>)
    const pd = productData.map(d => <Product key = {d.id}
                                             name = {d.name}
                                             description = {d.description}
                                             price = {d.price}/>)


  return (
      <div className="todo-list">
          {todoData}
      </div>
  )
}
*/

class App extends React.Component{

    constructor() {
        super();
        this.handleMouseOverOnImage = this.handleMouseOverOnImage.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.state = {
            name: "Bishwajit Purkaystha",
            age: "21",
            loggedIn: false,
            todoData: todoItemData.map(d => <TodoItem key = {d.key}
                                                      data = {d}
                                                      handleChange = {this.handleChange}/>)
        }
    }

    handleMouseOverOnImage() {
        console.log("Image is hovered...")
    }

    handleChange(key) {

        console.log('handleChange method called with key: ' + key)
        this.setState(prevState => {

            console.log('prev state: ' + JSON.stringify(prevState))
            let updatedState = prevState.todoData.map(todo => {
                if(todo.key === key)
                    todo.checked = !todo.checked
                console.log('todo: ' + JSON.stringify(todo))
                return todo
            })
            return {
                todoData: updatedState
            }
        })

    }

    render() {
        const todoData = todoItemData.map(d => <TodoItem
            key = {d.key}
            data = {d}
            handleChange = {this.handleChange}/>)
        return (
            <div className="todo-list">

                {todoData}
            </div>
        );
    }

}

export default App
